import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App: React.FC = () => {
  const [stockId, setStockId] = useState('');
  const [chartUrl, setChartUrl] = useState('');
  const [divergentStocks, setDivergentStocks] = useState<{ stockId: string; date: string }[]>([]);

  const fetchStockData = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/stock/${stockId}`);
      setChartUrl(response.data.chartUrl);
      setDivergentStocks(response.data.divergentStocks);
    } catch (error) {
      console.error('Error fetching stock data:', error);
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h1>台股 MACD 背離分析</h1>
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={stockId}
          onChange={(e) => setStockId(e.target.value)}
          placeholder="輸入股票代碼（例如 2330）"
          style={{ width: '300px', height: '40px', marginRight: '10px' }}
        />
        <button onClick={fetchStockData} style={{ height: '40px' }}>
          查詢
        </button>
      </div>
      <div style={{ display: 'flex' }}>
        <div style={{ width: '1000px', minHeight: '600px' }}>
          {chartUrl ? (
            <img src={chartUrl} alt="Stock Chart" style={{ width: '100%' }} />
          ) : (
            <p>請輸入股票代碼以查看圖表</p>
          )}
        </div>
        <div style={{ width: '400px', marginLeft: '20px' }}>
          <h3>背離股票清單</h3>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ border: '1px solid gray', padding: '5px' }}>股票代碼</th>
                <th style={{ border: '1px solid gray', padding: '5px' }}>背離日期</th>
              </tr>
            </thead>
            <tbody>
              {divergentStocks.map((stock, index) => (
                <tr key={index}>
                  <td style={{ border: '1px solid gray', padding: '5px' }}>{stock.stockId}</td>
                  <td style={{ border: '1px solid gray', padding: '5px' }}>{stock.date}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default App;
